import React from 'react'
import 'bulma/css/bulma.css'

class Form extends React.Component {


    constructor(props){
        super(props)
        this.state={
            type: "Asset",
            name: "",
            balance: ""
        }


        this.MIN_BALANCE = 0;
        this.MAX_BALANCE = 9999999;
    }

    isBalanceValid = (val) => {

        if(val < this.MIN_BALANCE || val > this.MAX_BALANCE)
        {
            return false;
        }

        return true
    }
    GetBalanceHelpText = () => {

        if(this.isBalanceValid(this.state.balance) == false)
        {
            return(
                <p class="help is-danger">
                    Value must be within range {this.MIN_BALANCE}...{this.MAX_BALANCE}
                </p>
            )
        }
    }

    onClickSubmit = () => {

        if(this.isBalanceValid(this.state.balance) == false ){
            console.log("invalid balance")
        }
        else {
            console.log("Submitting new review!")
        }
    }
    onSelectTypeChange = (event) => {
        const val = event.target.value;
        console.log(val)
        this.setState(prev =>{
            return{
                ...prev,
                type: val
            }
        })
    }
    onInputNameChange = (event) => {
        const val = event.target.value;
        console.log(val)
        this.setState(prev =>{
            return{
                ...prev,
                name: val
            }
        })
    }
    onInputBalanceChange = (event) => {
        const val = event.target.value;
        console.log(val)

        const input = document.getElementById("balance-input")
        if(this.isBalanceValid(val) == false)
        {
            input.classList.add("is-danger")
        }
        else {
            input.classList.remove("is-danger")
        }

        this.setState(prev =>{
            return{
                ...prev,
                balance: val
            }
        })
    }

    render(){
        return(
            <div className="form box">

                <div class="field">
                    <label class="label">Type</label>
                    <div class="control">
                        <div class="select">
                            <select onChange={this.onSelectTypeChange}>
                                <option>Asset</option>
                                <option>Liability</option>
                            </select>
                        </div>
                    </div>
                </div>

                <label class="label">Name</label>
                <p class="control has-icons-left">
                    <input class="input" type="text" placeholder="Name" onChange={this.onInputNameChange}/>
                    <span class="icon is-small is-left">
                        <i class="fa fa-user-circle"></i>
                    </span>
                </p>

                <label class="label">Balance</label>
                <p class="control has-icons-left">
                    <input id="balance-input" class="input" type="number" min="0" max="9999999" placeholder="Amount" onChange={this.onInputBalanceChange}/>
                    <span class="icon is-small is-left">
                        <i class="fa fa-dollar"></i>
                    </span>
                </p>

                {this.GetBalanceHelpText}

                <div className="field">
                    <p className="control">
                        <button className="button is-link" onClick={this.onClickSubmit}>Submit</button>
                    </p>
                </div>

            </div>
        )
    }
}

export default Form
