import React from 'react'
import '../css/mainContent.css'
import 'bulma/css/bulma.css'
import ReviewTableItem from './reviewTableItem'

class MainContent extends React.Component {

    constructor(props){
        super(props)
        props.reviews = []
    }

    renderTableRow = (item) => {
        console.log(item)
        return <ReviewTableItem props={item}/>
    }
    renderTableRows = () => {        
        return this.props.reviews.map(this.renderTableRow)
    }

    componentDidMount(){

        fetch("/reviews/get")
        .then(r => r.json())
        .then(data => {
            this.setState(prev => {
                return {
                    ...prev,
                    reviews: data
                }
            })
        })
    }


    render(){
        return(
            <div className="main-content">
                <div className="left-content">

                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Type</th>
                                    <th>Name</th>
                                    <th>Balance</th>
                                </tr>
                            </thead>
                            <tbody>                                
                                {this.renderTableRows()}
                            </tbody>
                        </table>
                </div>
                <div className="right-content">Right Side</div>
            </div>
        )
    }
}

export default MainContent
