import React from 'react'
import '../css/mainContent.css'
import 'bulma/css/bulma.css'
import ReviewTableItem from './reviewTableItem'
import Form from './Form'

class MainContent extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            reviews: [],
            formCounter: 0
        }
    }

    renderTableRow = (item) => {
        return <ReviewTableItem review={item}/>
    }
    renderTableRows = () => {        
        return this.state.reviews.map(this.renderTableRow)
    }

    componentDidMount(){

        fetch("/reviews/get")
        .then(r => r.json())
        .then(data => {
            this.setState(prev => {
                return {
                    ...prev,
                    reviews: data
                }
            })
        })
    }


    render(){
        return(
            <div className="main-content">
                <div className="left-content">                    
                    <table className="table is-striped is-hoverable">
                        <thead>
                            <tr>
                                <th>Type</th>
                                <th>Name</th>
                                <th>Balance</th>
                            </tr>
                        </thead>
                        <tbody>                                
                            {this.renderTableRows()}
                        </tbody>
                    </table>
                </div>
                <div className="right-content">
                    <p className="title right-title">Add New Item</p>
                    <Form setFormDirty={() => this.setState({formCounter: this.state.formCounter+1})}/>
                </div>
            </div>
        )
    }
}

export default MainContent
